package main
import (
	"fmt"
	"sort"
)

func threeSum(nums []int) [][]int {
	var res [][]int
	if len(nums) < 3 {
		return res
	}

	allZero := true
	for i := 0; i < len(nums); i++ {
		if nums[i] != 0 {
			allZero = false
		}
	}
	if allZero {
		return [][]int {[]int {0, 0, 0}}
	}

	sort.Ints(nums)
	cache := make(map[[2]int]bool)
	
	for i := 0; i < len(nums) - 2; i++ {
		if nums[i] > 0 {
			break
		}
		j, k := i + 1, len(nums) - 1
		for j < k {
			if nums[i] + nums[j] + nums[k] > 0 {
				k--
			} else if nums[i] + nums[j] + nums[k] < 0 {
				j++
			} else {
				if !cache[[2]int {nums[i], nums[j]}] {
					res = append(res,[]int {nums[i], nums[j], nums[k]})
					cache[[2]int {nums[i], nums[j]}] = true
				}
				
				j++
				k--
			}
		}
	}
	
	return res
}

